INSERT INTO `1_Templates` (`id`, `name`, `title`, `text`, `type_id`) VALUES
(31, 'Заказчику', 'Восстановление пароля', 'Тема: Восстановление пароля\r\nЗдравствуйте. Вы оформили запрос на восстановление пароля на сайте %сайт%\r\n\r\nДля изменения пароля перейдите по ссылке \r\n%ссылка на страницу изменения пароля%\r\n\r\nС уважением компания %компания%. \r\nЭто письмо сформировано роботом автоматически, отвечать на него не следует. \r\nЕсли у Вас возникли трудности или вопросы обратитесь на нашу почту тех поддержки %support%\r\n', 10),
(32, 'Заказчику', 'Заказчику после регистрации.', 'Тема: Успешная регистрация в компании %компания% \r\nЗдравствуйте %имя%. Благодарим за регистрацию в %компания% \r\n\r\nВаши  учётные данные для входа в систему: \r\nЛогин: %login%\r\nПароль: %password%\r\n\r\nВы можете войти в Ваш личный кабинет по ссылке %ссылка на страницу личного кабинета%\r\n\r\nС уважением, %support%', 11),
(33, 'Заказчику', 'Заказчику проект принят', 'Тема: Регистрация проекта\r\n\r\nЗдравствуйте. Ваш проект успешно зарегистрирован на сайте %сайт%\r\nБлагодарим за заказ!\r\n\r\nВы можете просматривать выполнения Вашего заказа по ссылке: %Ссылка на заказ%\r\nНомер Вашего заказа : %№ заказа%\r\n\r\nНаш менеджер свяжется с Вами в течение 10 минут\r\n\r\nТак же мы будем Вас информировать о состоянии заказа по мере его выполнения.\r\n\r\nС уважением,\r\nкомпания %компания%', 12),
(34, 'Заказчику', 'Заказчику об оплате когда выставлен счет', 'Тема: Оплата заказа\r\n\r\nЗдравствуйте!\r\n\r\nПо Вашему заказу %№ заказа% %тема работы% Вам выставлен счет к оплате\r\n\r\nСумма к оплате: %сумма к оплате% рублей.\r\n\r\nДля того что бы оплатить перейдите по ссылке: %ссылка на страницу оплаты%\r\n\r\nСпасибо!', 13),
(35, 'Заказчику', 'Заказчику когда готова часть', 'Тема: По Вашему заказу %тема заказа% появилась готовая часть\\вся работа\r\n\r\nЗдравствуйте %имя%!\r\n\r\nПо Вашему заказу %ссылка на заказ% готова часть работы\\вся работа.\r\n\r\nВы можете ознакомится с результатом перейдя по ссылке\r\nСпасибо\r\n', 14),
(36, 'Заказчику', 'Заказчику когда готова вся работа.', 'Тема: Работа готова\r\nЗдравствуйте %имя%. \r\n\r\nПо Вашему заказу %ссылка на заказ% вся работа %название работы%\r\n\r\nГотовый материал будет Вам отправлен после полной оплаты заказа.\r\nСсылка для входа на сайт : \r\n\r\nПри возникновении замечаний Вы можете создать заявку на корректировку работы и мы обязательно выполним ее.\r\n\r\nСпасибо за доверие к нашей компании.\r\n', 15),
(37, 'Заказчику', 'Заказчику о сообщении в чате', 'Тема: Сообщение в чате\r\nЗдравствуйте %имя%. \r\nВам оставили сообщение в чате Вашего заказа %№ заказа%\r\n%текст сообщения%\r\nЧтобы ответить на сообщение, перейдите по ссылке.\r\nСсылка на заказ: %ссылка на заказ% \r\nПросьба не отвечать на сообщение ответом на это письмо, отправитель его не получит.', 16),
(38, 'Заказчику', 'Заказчику о завершении заказа', 'Тема: Заказ завершен\r\nЗдравствуйте %имя%.\r\n\r\nВаш заказ номер %номер заказа% завершен.\r\n\r\nВ случае необходимости восстановления заказа, обратитесь к менеджеру.\r\n\r\nСпасибо за доверие к нашей компании. \r\nС нетерпением ждем новых заказов от Вас ;)', 17),
(39, 'Исполнителю', 'Исполнителю сообщение рассылки', 'Появился %новый заказ% по Вашей специальности (%специальность%)\r\nЗдравствуйте %имя%\r\nОповещаем Вас о поступлении нового заказа.\r\n\r\nНаименование: %Наименование% \r\nСпециальность: %специальность%\r\n\r\nПодробнее узнать о заказе, задать уточняющие вопросы, сообщить о Вашем желании стать Исполнителем этой работы Вы можете в чате, перейдя по этой ссылке,  Предварительно авторизируйтесь в своем личном кабинете (для того что бы каждый раз не авторизовываться поставьте галочку "Запомнить" при авторизации).\r\nЕсли Вы хотите стать автором этой работы, напишите в чат желаемую цену и в течении суток Вы получите ответ о результате. Если Вы делали заявку три раза, указывая при этом адекватную сложности заказа и своему рейтингу цену и Вас не назначили на заказ, подпишите менеджеру "я Исполнитель на очереди" и Вам дадут этот заказ.\r\nПожалуйста, внимательно просмотрите всю информацию о заказе, сроках и подробности написания работы!\r\nО назначении Вас исполнителем заказа Вы будете оповещены по электронной почте\r\n', 18),
(40, 'Исполнителю', 'Исполнителю о назначении', 'Здравствуйте %имя%!\r\nВы назначены Исполнителем заказа %наименование%.\r\nВаш бюджет составит %из поля стоимость% рублей.\r\nЕще раз внимательно изучите форму заказа и сообщите менеджеру, что Вы приступаете к написанию работы. Работу необходимо сдавать по частям, соблюдая сроки сдачи каждой части указанные в заказе\r\n.\r\nПожалуйста, проверяйте Вашу почту и чат не реже одного раза в день, чтобы оперативно отвечать на сообщения от клиента и менеджера. Если Вы по каким-либо причинам не можете закончить работу или задерживаете со сроками - уведомите об этом менеджера заранее. За нарушение сроков сдачи без уважительной причины применяются штрафные санкции!\r\nЕсли Вы не сдаете вовремя работу, не оповестив нас об этом заранее, и при этом не выходите на связь мы будем вынуждены снять Вас с этого и остальных заказов, снизить Вам рейтинг ниже нуля и прекратить с Вами сотрудничество.\r\nНадеемся на Ваше понимание ответственности перед Заказчиком!\r\nПосле успешной сдачи каждого заказа, у Вас повышается персональной рейтинг Исполнителя. Чем выше рейтинг – тем чаще Вас будут назначать исполнителем на  заказы. Обладателям высокого рейтинга выплачиваются премии. Мы дорожим нашими Исполнителями\r\n\r\n Пожалуйста, ОБЯЗАТЕЛЬНО ПОДТВЕРДИТЕ ВЫПОЛНЕНИЕ ЗАКАЗА В ТЕЧЕНИЕ СУТОК!\r\n\r\n', 19),
(41, 'Исполнителю', 'Исполнителю о сообщении в чате', 'Тема: Сообщение в чате\r\nЗдравствуйте %имя%. \r\nВам оставили сообщение в чате Вашего заказа %№ заказа%\r\n%текст сообщения%\r\nЧтобы ответить на это сообщение, перейдите по ссылке .\r\nСсылка на заказ: %ссылка на заказ% \r\nПросьба не отвечать на сообщение ответом на это письмо, отправитель его не получит.\r\n', 20),
(42, 'Исполнителю', 'Исполнителю о съеме с заказа', '', 21),
(43, 'Исполнителю', 'Исполнителю о том что срок сдачи части наступил', 'Здравствуйте %имя%.\r\n\r\nНапоминаем Вам что сегодня-  срок сдачи части %название части% по заказу %ссылка на заказ%. \r\n\r\nЕсли Вы по каким-либо причинам не можете закончить работу или задерживаете со сроками - уведомите об этом менеджера заранее.\r\nСпасибо за Вашу ответственность.', 22),
(44, 'Исполнителю', 'Исполнителю о новой доработке', 'Здравствуйте %имя%.\r\n\r\nОбращаем Ваше внимание  \r\nЗаказчик загрузил замечания в форму заказа %ссылка на заказ%, требуется корректировка одной из частей.\r\nЗамечания Вы можете найти в форме заказа в разделе “Доработки”. \r\n\r\nТакже обратите внимание на блок “Части”, данные доработки будут добавлены как новая часть и будет установлен срок их сдачи.\r\n\r\n\r\n', 23),
(45, 'Исполнителю', 'Исполнителю об оплате заказа', 'Здравствуйте %Имя%\r\n\r\nПо заказу %ссылка на заказ% выставлено к оплате %сумма из выставлено к оплате% рублей.\r\n\r\nОжидайте поступления средств на Ваш счет.', 24);
