INSERT INTO `1_Templates` (`id`, `name`, `title`, `text`, `type_id`) VALUES
(2, 'Заказчику', 'когда не можем дозвониться', 'К сожалению, не можем дозвониться на указанный в заказе телефон, пожалуйста, оставьте актуальный номер телефона в чате заказа или наберите нас + 7 (495) 504 37 19, необходимо обсудить детали Вашего заказа. Спасибо за доверие к нашей компании!', 1),
(3, 'Заказчику', 'если не оставил телефон', 'Здравствуйте!\r\n\r\nВы оформили заявку на сайте http://adco.obshya.com/\r\n\r\nСпасибо за проявленный интерес к нашей компании!\r\n\r\nРаботу по Вашему заказу выполним! \r\n\r\nОриентировочная цена такой работы: от …\r\n\r\nВы можете задать свой вопрос в Чате Вашего заказа (предварительно авторизовавшись на сайте) или перезвонить нам по телефону: +7(495) 504-37-19.\r\n\r\nТакже, для уточнения подробностей, Вы можете оставить свой контактный телефон - и мы с удовольствием Вам перезвоним и проконсультируем Вас по всем вопросам :).', 1),
(4, 'Заказчику', 'спрашивает, что с работой', 'Здравствуйте!\r\n\r\nС Вашим заказом все в порядке. Исполнитель работает.\r\n\r\nРабота будет выполнена в срок, указанный в заявке (сроки сдачи частей работы остаются на усмотрение исполнителя)\r\n\r\n', 1),
(5, 'Исполнителю', 'просьба зайти в чат', 'Просьба почаще проверять чат по взятым заказам и вовремя отвечать на сообщения, чтобы была понятна ситуация по заказу. Это не займет много времени. Спасибо', 2),
(6, 'Заказчику', 'просит больше 30% от работы', 'Здравствуйте.\r\n\r\nПеред началом работы Вы внесли предоплату в размере 50% от стоимости Вашего заказа.\r\n\r\nПо оценке Исполнителя, запрашиваемая Вами часть превышает сумму оплаченной стоимости. Такие выводы сделаны на основании сложности работы и объема, которые занимает около 60-70% всего проекта.\r\n\r\nПросим Вас оплатить оставшуюся сумму, после чего мы сможем выслать запрашиваемую Вами часть работы для ознакомления. Если же оплата оставшейся суммы не представляется возможным, то можем выслать, для ознакомления, любую другую часть из Вашей работы, исключая самую крупную и трудоемкую.', 1),
(7, 'Заказчику', 'доработки выходят за рамки первонач заказа', 'Здравствуйте!\r\n\r\nПо условию нашего Договора, мы обязуемся делать бесплатные доработки только в рамках первоначальных требований.\r\n\r\nДоработки, которые Вы требуете сейчас, расходятся с изначальным заданием, поэтому по этим доработкам требуется доплата в размере ... рублей. Далее прикреплен файл, заполненный Исполнителем, который отображает эти расхождения.\r\n\r\nС уважением, компания Админтрикс', 1),
(8, 'Заказчику', 'после получения предоплаты', 'Здравствуйте!\r\n\r\nОплату получили. Приступаем к работе.\r\n\r\nВся работа будет сделана в срок, указанный в Форме Заказа и выслана Вам в Чат; сроки сдачи частей остаются на усмотрение Исполнителя, если другое не было оговорено в заказе.\r\n\r\nЕсли у Вас появятся дополнительные материалы по работе, Вам нужно прикрепить их к заявке. В случае возникновения вопросов по работе, Вы можете написать сообщение через данный Чат, адресуя Исполнителю: Исполнитель отвечает в течении суток (по рабочим дням).\r\n\r\nТак же, если Исполнитель не ответил Вам в течении суток, или у Вас есть вопросы по оплате, Вы можете адресовать вопросы Менеджеру: Менеджер отвечает в течении 5 часов (не забудьте просматривать почту, на которую Вам будет приходит уведомление о новом сообщении в Чате). В связи с высокой загруженностью менеджеров, и стремлением фиксировать все вопросы по заказу, всё общение происходит исключительно через форму Чата. Надеемся на Ваше понимание.\r\n\r\nСпасибо, что воспользовались услугами нашей компании! :)', 1),
(9, 'Заказчику', 'спрашивает о корректировках', 'Здравствуйте!\r\n\r\nИсполнитель выполняет корректировки. Сроки на выполнение доработок, остаются на усмотрение Исполнителя  (естественно, в разумных пределах, в зависимости от объема работы).\r\n\r\nУточнить сроки, Вы можете напрямую у Исполнителя, адресовав ему сообщение в этот Чат! Исполнитель отвечает в чате в течение суток (по рабочим дням). Если автор Вам не ответил в течении этого времени, пожалуйста, напишите в Чат менеджеру, что автор не отвечает - Менеджер поторопит автора с ответом!\r\n\r\nСпасибо!', 1),
(10, 'Заказчику', 'спрашиваем об утверждении части', 'Здравствуйте!\r\n Утверждена ли отправленная часть заказа? Может ли Исполнитель продолжать работу?\r\nПожалуйста, дайте знать об этом Исполнителю   в чате. С уважением, компания Админтрикс', 1),
(11, 'Заказчику', 'можно ли закрывать заказ', 'Здравствуйте!\r\n Нужны ли корректировки по заказу или его можно завершить?Дайте об этом знать исполнителю в чате.\r\n С уважением, компания Админтрикс.', 1),
(12, 'Заказчику', 'возврат одобрен', 'Здравствуйте!\r\n Возврат средств по Вашему заказу рассмотрен руководством компании Админтрикс и одобрен.\r\nДля получения средств, Вам необходимо предоставить Ваши банковские реквизиты, такие как: - ФИО - БИК или Наименование банка - ИНН - Номер счета.\r\n После получения этой информации, возврат будет произведен в течение 7-х рабочих дней. *Примечание: Если Вы ранее указывали реквизиты и получили это письмо, значит реквизиты были указаны частично. Например: Был указан Номер банковской карты, но не указан ИНН получателя. \r\nС уважением, компания Админтрикс', 1),
(13, 'Заказчику', 'если зак угрожает', 'Здравствуйте!\r\nСообщаем Вам, что мы осуществляем деятельность в пределах действующего законодательства РФ и Договора публичной оферты (Пользовательское Соглашение).\r\n Мы выполнили  работу в полном объёме. От доработок не отказываемся и готовы их предоставить. Нарушения Договора не усматриваем. \r\nКомпания Админтрикс', 1),
(14, 'Заказчику', 'возврат на рассмотрении', 'Здравствуйте! \r\nВаш запрос отправлен на рассмотрение к руководству, окончательный вердикт будет вынесен в течение недели. Пожалуйста, ожидайте, мы обязательно решим Ваш вопрос в максимально короткие сроки. \r\nС уважением, компания Админтрикс', 1),
(15, 'Заказчику', 'в возврате отказано', 'Здравствуйте!\r\nСообщаем, что возврат средств по Вашему заказу невозможен, так как работа была выполнена в срок, указанный в заказе. Если нужны доработки в рамках первоначальных требований, то Исполнитель внесет их бесплатно. Компания Админтрикс', 1),
(16, 'Заказчику', 'корректировки, если заказ давно закрыт', 'Здравствуйте! Данный заказ был закрыт 9 апреля 2015 года, гарантийный срок на выполнение корректировок истек, сейчас корректировки возможно внести за дополнительную плату. Напишите исчерпывающий список замечаний к работе и Исполнитель рассчитает стоимость корректировок. С уважением, компания Админтрикс', 1),
(17, 'Исполнителю', 'порядок закрытия заказа', 'Заказ завершается через неделю после того, как заказчик заберет работу, если в течение этой недели не поступят от него замечания. В случае наличия замечаний- через неделю после выполнения корректировок.', 2),
(18, 'addzakazavtor', 'Автору: Назначен на заказ', 'Здравствуйте!\r\n\r\nВы назначены Автором этого заказа.\r\n\r\nВаш бюджет составит 500 рублей.\r\n\r\nЕще раз внимательно изучите форму заказа и сообщите менеджеру, что Вы приступаете к написанию работы.\r\n\r\nПожалуйста, проверяйте Вашу почту и чат раз в день, чтобы оперативно отвечать на сообщения от клиента и менеджера. Если Вы по каким-либо причинам не можете закончить работу или задерживаете со сроками - уведомите об этом менеджера заренее. За просрочку работ, без уважительной причины штрафные санкции!\r\n\r\nЕсли Вы задержали работу и не сообщили об этом заранее, и не выходите на связь, мы будем вынуждены снять Вас с этого и остальных заказов, снизить Вам рейтинг ниже нуля и прекратить с Вами сотрудничество\r\n\r\nНадеемся на Ваше понимание отвественности перед Заказчиком!\r\n\r\nПосле утверждния всей работы, оплата будет произведена оплата за заказ на кошельки (WMR и Яндекс .деньги)\r\n\r\nПосле успешной сдачи каждого заказа, у Вас повышается персональной рейтинг Автора. Чем выше рейтинг – тем чаще Вас будут назначать автором работ. Обладателям высокого рейтинга выплачиваются премии. Мы дорожим нашими Авторами с высоким рейтингом! Пожалуйста, ОБЯЗАТЕЛЬНО ПОДТВЕРДИТЕ ВЫПОЛНЕНИЕ ЗАКАЗА В ТЕЧЕНИЕ СУТОК!', 2),
(19, 'Исполнителю', 'если отказывается от доработок', 'Здравствуйте!\r\n\r\nЕсли, по Вашему мнению, Заказчик изменил (дополнил) первоначальные требования, то, по условиям нашей работы, Вы вправе затребовать дополнительную оплату за заказ. В этом случае, Вам нужно все измениния расписать и выслать клиенту.\r\n\r\nЕсли Клиент/ Менеджер подтвердят это, то Вам будет выплачен дополнительный гонорар. Если же изменений в изначальном ТЗ нет, то Вы как автор, который обязался выполнять работу и делать бесплатные корректировки (что было указано в нашем Соглашении), должны выполнить бесплатно. В случае если доработоки не произведете, то оплаты за заказ не будет.', 2),
(20, 'Исполнителю', 'если не выслал в срок', 'Здравствуйте!\r\n\r\nПо заказу № {id} Вы должны были сдать работу (..)!\r\n\r\nПожалуйста, срочно напиши в чате и вышлите работу, сегодня! В противном случае, в соответствии с нашим соглашением, мы будем вынуждены Вас оштрафовать и сильно понизить рейтинг! Компания ДипСтарт.', 2),
(21, 'Исполнителю', 'срок сдачи работы', 'Напоминаем - {} срок сдачи всей работы. Пожалуйста, подтвердите дату.', 2),
(22, 'Исполнителю', 'спрашивает, что с работой', 'Работа находится на проверке у заказчика и ещё не утверждена. Как только будут результаты проверки - мы Вам сразу же сообщим. С уважением, компания Админтрикс', 2),
(23, 'Исполнителю', 'когда оплачивают', 'Здравствуйте! Оплата за заказы происходит после того, как Ваш заказ отправлен в бухгалтерию (о том, когда он будет завершен - вы можете уточнить у менеджера в чате). Если из Вашего личного кабинета на сайте  пропал какой-либо заказ - значит он отправлен в бухгалтерию и вскоре будет оплачен. В данный момент оплата происходит в течении всей недели, по рабочим дням. Компания Админтрикс', 2),
(24, 'Исполнителю', 'когда снимаем автора за нарушение', 'Корректировок нет, согласно нашим условиям мы вынуждены снять Вас с заказа, без дальнейшей оплаты. Вы можете в ближайшее время отправить в чат заказа откорректированную работу, в таком случае будуте назначены на заказ опять и гонорар будет выплачен. С уважением, компания Админтрикс', 2),
(25, 'Исполнителю', 'когда аванс после плана', 'Здравствуйте! Предварительно Вы назначены на заказ, но окончательное решение о сотрудничестве заказчик примет после рассмотрения плана к работе. Ждем план работы до 16.08.2015. Подтвердите, пожалуйста, начало работы в течение суток.', 2),
(26, 'Заказчику', 'после отправки плана (если без аванса) ', 'Здравствуйте. Стоимость работы рублей. Для того, чтобы наш Автор смог продолжить выполнение Вашего заказа, Вам необходимо внести оплату 50% от стоимости работы. После оплаты сделайте, пожалуйста, скриншот или фото квитанции и отправьте в чат, после этого Исполнитель продолжит выполнение Вашей работы. С уважением, компания Админтрикс', 1),
(27, 'Заказчику', 'когда просит корректировки спустя много времени', 'Здравствуйте! Данный заказ был закрыт 9 апреля 2015 года, гарантийный срок на выполнение корректировок истек, сейчас корректировки возможно внести за дополнительную плату. Напишите исчерпывающий список замечаний к работе и автор рассчитает стоимость корректировок. С уважением, компания Админтрикс', 1),
(28, 'Исполнителю', 'спрашивает, когда закроют', 'Заказ завершается через неделю после того, как заказчик заберет работу, если в течение этой недели не поступят от него замечания. В случае наличия замечаний- через неделю после выполнения корректировок.', 2),
(29, 'Заказчику', 'когда ждем предоплату', 'Номер Вашего заказа 89. Для того чтобы исполнитель приступил к работе, ждем оплату. После осуществления платежа обязательно сохраняйте чек, затем сделайте его фото и вышлите в чат - по нему будет проверяться поступление платежа. Компания Админтрикс', 1),
(30, 'Заказчику', 'все ли устраивает', 'Здравствуйте! Устраивает ли работа, выполненная автором, можно закрывать заказ  или нужны доработки?', 1),
(31, 'автору', 'спрашивает, актуален ли заказ', 'да, заказ актуален. изучите, пожалуйста, тз и предлагайте Ваш бюджет', 2);
